/*-------------------------------------------------------------------------------------------------+
>>  삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelete(obj) {

	var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";
	var strBrncCd	 = "";
	if( tabGbn.TabIndex == 0){

		if( dsTsnResult.GetRowCount() == 0){
			fnc_Message(SNIS_COM_1003, "N");
			return;
		}
		var nCnt = 0;
		dsTsnResult.fireEvent = false;
		dsTsnResult.Filter("CHK='1'");
		nCnt = dsTsnResult.GetRowCount();
		dsTsnResult.UnFilter();
		dsTsnResult.fireEvent = true;

		if( nCnt < 1 ){
			fnc_Message(SNIS_COM_1003,"N");
			return;
		}

		if( !fnc_Message(L_COM_CNF_0004,"Y") ) return;


		// 지불정지만 존재에서 삭제
		sServiceName = "rsm6030-service:deleteTsn";
		sInDataSet   = "dsTsnResult=dsTsnResult";
		OutDataSet  = "";

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

	}
}
