/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	if( nCell == 0){
		var strVal = obj.GetCellProp("Head",0,"Text");
		var strHval;
		if( strVal == "1"){
			obj.SetCellProp("Head",0,"Text",0);	
			strHval = 0;
		}else{
			obj.SetCellProp("Head",0,"Text",1);	
			strHval = 1;
		}
		dsTsnResult.fireEvent = false;
		for(var i=0;i<dsTsnResult.rowcount;i++){
			dsTsnResult.SetColumn(i,"CHK",strHval);
		}		
		dsTsnResult.fireEvent = true;

	}else{
		//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
		fnc_GridSort(obj, object(obj.BindDataset), nCell);
	}
}

