/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    if( sServiceName == "rsm6030-service:deleteTsn") {
		fnc_Message(SNIS_CRA_B999, "N","삭제가 완료되었습니다!");
		fcSearch();

    }
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/

function dsVeriCd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if( nReason == 0){
		obj.fireEvent = false;
		obj.filter("CD!='001'");
		dsVeriCd_TAB.CopyF(obj);
		obj.unFilter();
		obj.fireEvent = true;

		dsVeriCd_TAB.InsertRow(0);
		dsVeriCd_TAB.SetColumn(0, "CD_NM", "전체");

		TabGbn.tab2.cbVeriCd.index = 0;
	}
}

function dsTsnResult_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "CHK"){
		obj.fireEvent = false;
		obj.Filter("CHK=='0'");
		var nCnt = obj.rowcount;
		obj.Unfilter();
		obj.fireEvent = true;

		if( nCnt == 0)	TabGbn.tab1.grdTsn.SetCellProp("head",0,"Text","1");
		else TabGbn.tab1.grdTsn.SetCellProp("head",0,"Text","0");		
	}
}

function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{

	if( obj.id == "dsBrncCd"){
		 if ( nReason == 0){
			obj.fireEvent = false;
			var nRow  = obj.addrow();
			obj.SetColumn(nRow,"CD","01");
			obj.SetColumn(nRow,"CD_NM","광명");

			nRow  = obj.addrow();
			obj.SetColumn(nRow,"CD","02");
			obj.SetColumn(nRow,"CD_NM","광명");

			nRow  = obj.addrow();
			obj.SetColumn(nRow,"CD","03");
			obj.SetColumn(nRow,"CD_NM","광명");

			nRow  = obj.addrow();
			obj.SetColumn(nRow,"CD","04");
			obj.SetColumn(nRow,"CD_NM","광명");

			nRow  = obj.addrow();
			obj.SetColumn(nRow,"CD","08");
			obj.SetColumn(nRow,"CD_NM","광명");

			obj.fireEvent = true;
		}
	}else if(obj.id == "dsBrncCdSearch"){
			dsBrncCdSearch.InsertRow(0);
			dsBrncCdSearch.SetColumn(0, "CD_NM", "전체");
			TabGbn.tab1.cbBrncCd.index = 0;
			TabGbn.tab2.cbBrncCd.index = 0;
			break;	
	}
}

function tabGbn_Tab1_grdTsn_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
   if(obj.GetCellPos() == 1){
		if((bCtrl) && (nChar == 17))
		{
			ClearClipboard();
			SetClipBoard("CF_TEXT",obj.GetCellText("body",dsTsnResult.row,obj.GetCellPos()));
		}
   }
}

function tabGbn_tab2_grdTsnDas_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{ 
   if(obj.GetCellPos() == 2){
		if((bCtrl) && (nChar == 17))
		{
			ClearClipboard();
			SetClipBoard("CF_TEXT",obj.GetCellText("body",dsTsnResultDas.row,obj.GetCellPos()));
		}
   }

}
