	fcInit(obj);

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	if ( !fcSearchCheck(obj) ) return;

    var strstatus	 = cbStatus.Value;
	var sServiceName = "rsm6050-service:searchTsnReceipt";
    var sInDataSet   = "";
    var sOutDataSet  = "dsTsnRcptResult=dsTsnRcptResult ";

    var brnccd = "";

    if( cbBrncCd.Value == "00"){
		brnccd = "^(0[012348])$";
	}else{
		brnccd = cbBrncCd.Value;
	}

	fcd_AddParam("tnsno"		, edtTsn.Text);
	fcd_AddParam("brnccd"		, brnccd);

	fcd_AddParam("fromdate"		, calStopFromDate.Value );
	fcd_AddParam("todate"		, calStopToDate.Value);
	fcd_AddParam("daytype"      , rdoDayType.Value);	

	if( strstatus == "000"){
		fcd_AddParam("ticstas"	, "^(00[123])$");	// 전체일때 정규식으로 처리한다.
	}else{
		fcd_AddParam("ticstas"	, strStatus);	
	}	


	dsTsnRcptResult_S.ClearData();
	dsTsnRcptResult.row = -1;
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 저장가능유무처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {  

	if( fnc_IsNull(cbAcdntTycd.Value) ){				
		fnc_Message(SNIS_COM_1035, "N","사고유형");
		cbAcdntTycd.SetFocus();
		return false;
	}
	if( fnc_IsNull(cbRefundTycd.Value) ){	
		fnc_Message(SNIS_COM_1035, "N","환급유형");
		cbRefundTycd.SetFocus();
		return false;
	}
	if( fnc_IsNull(cbBrnc.Value) ){			
		fnc_Message(SNIS_COM_1035, "N","본점/지점");
		cbBrnc.SetFocus();
		return false;
	}
	if( fnc_IsNull(edtCustnm.Text) ){			
		fnc_Message(SNIS_COM_1035, "N","고객성명");
		edtCustnm.SetFocus();
		return false;
	}
	// if( fnc_IsNull(calBirthday.Value) ){			
		// fnc_Message(SNIS_COM_1035, "N","고객생년월일");
		// calBirthday.SetFocus();
		// return false;
	// }
	// if( fnc_IsNull(edtCustPhone.Text) ){			
		// fnc_Message(SNIS_COM_1035, "N","고객전화");
		// edtCustPhone.SetFocus();
		// return false;
	// }




	if(dsTsnRcptResult_S.GetColumn(dsTsnRcptResult_S.row,"TIC_STAS") != "001" && dsUploadFile.GetRowCount() <=0){
		fnc_Message(SNIS_RBM_D018, "N");
		return false;	
	}

	return true;
}

function fcSaveReqCheck(obj) {

	if(dsUploadFile.GetRowCount() <=0){
		fnc_Message(SNIS_RBM_D017, "N");
		return false;	
	}

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	if( dsTsnRcptResult_S.rowcount == 0) return;

    if ( !fcSaveCheck() ) return;   

    if( !fnc_Message(SNIS_COM_1012, "Y")) return;


    //첨부파일 서버 업로드 
	if( dsUploadFile.GetRowCount() > 0 ) {
		for ( i = 0; i < dsUploadFile.GetRowCount(); i++ ) {
			if(dsUploadFile.getColumn(i, "CHK") == "1" ) {
				var rtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
											, dsUploadFile.GetColumn(i, "FILE_PATH" )
											, dsUploadFile.GetColumn(i, "FILE_NM" ) 
											,"");

				var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsUploadFile.GetColumn(i, "FILE_PATH" ));

				if ( rtn_arr[0] != "SUCC" ) {
					alert(rtn_arr[1] + "파일 업로드 에러발생");
					return false;
				}


				dsUploadFile.SetColumn(i, "FILE_PATH"    , rtn_arr[1] );
				dsUploadFile.SetColumn(i, "FILE_SIZE"   , fileSize );

			} // end if
		}
	} // end 


	var sServiceName = "rsm6050-service:saveReceiptData";
	var sInDataSet   = "dsTsnRcptResult=dsTsnRcptResult_S dsUploadFile=dsUploadFile:u";
	var sOutDataSet  = "";  	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}



