***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
Trace("fcCallBack:ErrorCode:"+ErrorCode+", ErrorMSG:"+ErrorMSG);
    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;    

    if( sServiceName == "rsm6050-service:searchTsnReceipt"){

		if( dsTsnRcptResult.rowcount == 0){
			btnRcpt.Enable			= false; 	// 접수
			btnRcptCancel.Enable	= false; 	// 접수취소					
			btnCnclReq.Enable		= false;	// 해지요청
			btnCnclReqCancel.Enable = false;	// 해지요청취소
		}else{
			dsTsnRcptResult.row = -1;
			dsTsnRcptResult.row = 0;
		}

    }


    if( sServiceName ==   "rsm6050-service:saveReceipt"){		
		fnc_Message(SNIS_CRA_B999, "N","접수처리가 완료되었습니다!");
		cbStatus.Value = "002";
		fcSearch();

    }
    if( sServiceName ==   "rsm6050-service:saveReceiptCancel"){		
		fnc_Message(SNIS_CRA_B999, "N","접수취소 처리가 완료되었습니다!");
		cbStatus.Value = "001";
		fcSearch();

    }
    if( sServiceName ==   "rsm6050-service:saveCnclReq"){		
		fnc_Message(SNIS_CRA_B999, "N","해지요청 처리가 완료되었습니다!");

		var strDesc = "";
		strDesc = "TSN : "+ dsTsnRcptResult_S.GetColumn(0,"TSN_NO") +chr(10);
		strDesc += "분실신고번호 : "+ dsTsnRcptResult_S.GetColumn(0,"MISS_REPO_NO");

		if( rdoSend.Value == "001") fcSendMngMsg(strDesc);     // 사용함
		//else if( rdoSend.Value == "002" )	fcSendSms(strDesc);// SMS


		cbStatus.Value = "003";
		fcSearch();

    }
    if( sServiceName ==   "rsm6050-service:saveCnclReqCancel"){		
		fnc_Message(SNIS_CRA_B999, "N","해지요청 취소처리가 완료되었습니다!");
		cbStatus.Value = "002";
		fcSearch();

    }   

    if( sServiceName == "rsm6050-service:saveReceiptData"){		
		fnc_Message(SNIS_CRA_B999, "N","저장이 완료되었습니다!");		
		fcSearch();
    }
}

