// row변경시 접수상세데이타를 가져온다.
function dsTsnRcptResult_OnRowPosChanged(obj,nOldRow,nRow)
{		
	dsTsnRcptResult_S.ClearData();
	if( obj.row < 0) return;
	var nRow = dsTsnRcptResult_S.AddRow();	
	dsTsnRcptResult_S.CopyRow(nRow,"dsTsnRcptResult",dsTsnRcptResult.row);

	switch(dsTsnRcptResult_S.getcolumn(nRow,"TIC_STAS"))
	{
		case "001" : 	// 지불정지
			btnRcpt.Enable			= true; 	// 접수
			btnRcptCancel.Enable	= false; 	// 접수취소					
			btnCnclReq.Enable		= false;	// 해지요청
			btnCnclReqCancel.Enable = false;	// 해지요청취소
			fileBtnRow.Visible = true;

			// default값 처리(2012.04.02)
			cbAcdntTycd.Value = "001";
			cbRefundTycd.Value = "001";

			break;
		case "002" :	// 접수
			btnRcpt.Enable			= false; 	// 접수
			btnRcptCancel.Enable	= true; 	// 접수취소					
			btnCnclReq.Enable		= true;		// 해지요청
			btnCnclReqCancel.Enable = false;	// 해지요청취소
			fileBtnRow.Visible = true;

		  break;
		case "003" :	// 해지요청
			btnRcpt.Enable			= false; 	// 접수
			btnRcptCancel.Enable	= false; 	// 접수취소					
			btnCnclReq.Enable		= false;	// 해지요청
			btnCnclReqCancel.Enable = true;		// 해지요청취소
			fileBtnRow.Visible = false;

		  break;	  
	  default :			  
	}


	//첨부파일 조회
	if(nRow>-1){	
		fcFileSearch();
	}

}


//첨부파일 조회
function fcFileSearch(){

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	//첨부파일 조회 
	 sServiceName = "rsm6050-service:searchFileMana";
	 sInDataSet   = "";
	 sOutDataSet  = "dsUploadFile=dsUploadFile ";

	 //조회조건 : 
	 fcd_AddParam("ATT_FILE_KEY", dsTsnRcptResult.GetColumn(dsTsnRcptResult.row, "ATT_FILE_KEY"  ));

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

//쪽지 sms 발송 
function fcSendMngMsg(strDesc){

	  var sSendUserId = gdsUser.GetColumn(0,"USER_ID");
	  var sSendUserNm = gdsUser.GetColumn(0,"USER_NM");
	  var sSendUserHpNo = gdsUser.GetColumn(0,"HP_NO");

	  var sRecvUser = "";
	  var sTitle	= "지불정지 해지 요청! 확인 바랍니다.";
	  var sDesc		= strDesc;


	  fcd_SendMsgByAlarmDs("004", sSendUserId, sSendUserNm, sSendUserHpNo, sTitle, sDesc, "RSM6050" );

}

// 쪽지발송(old)
function fcSendmemo(strDesc)
{
	if ( dsAlarm.rowcount == 0 ) {
		fnc_Message(SNIS_CRA_Z003, "N");
		return false;
	}

	var sSendUser  	= gdsUser.GetColumn(0,"USER_ID");	 //송신자사번 
	var sRecvUser   = "";//수신자사번
	var sTitle   	= "지불정지 해지 요청! 확인 바랍니다.";//제목 
	var sLinkUrl 	= "";							 
	var sDesc   	= "";							 
	var sCompId     = "";

	for(var i = 0; i <dsAlarm.GetRowCount();i++){					
		sRecvUser +=  dsAlarm.GetColumn(i,"USER_ID")+",";														
	}

	sRecvUser = substr(sRecvUser,0,length(sRecvUser)-1);

	sParams = "sSendUser="	+quote(sSendUser);
	sParams += " sRecvUser="+quote(sRecvUser);
	sParams += " sTitle="	+quote(sTitle);
	sParams += " sLinkUrl="	+quote(sLinkUrl);
	sParams += " sDesc="	+quote(strDesc);
	sParams += " sCompId="	+quote(sCompId);

	Dialog("common::comSendMessage.xml", sParams, 192, 552, "Title=false");
}

//sms발송(old)
function fcSendSms(strDesc)
{
	if ( dsAlarm.rowcount == 0 ) {
		fnc_Message(SNIS_CRA_Z003, "N");
		return false;
	}


	var sReceivePhoneNumber = array(-1);
	var sReceiveName = array(-1);

	for(var i = 0; i <dsAlarm.GetRowCount();i++){

		sReceivePhoneNumber[i] =  dsAlarm.GetColumn(i,"HP_NO");
		sReceiveName[i] =  dsAlarm.GetColumn(i,"USER_NM");

	}

	for(var i = 0; i < length(sReceivePhoneNumber); i++){

	  fcd_SendSms(	  gdsUser.GetColumn(0,"HP_NO")    	// 송신자 번호
					, gdsUser.GetColumn(0,"USER_ID")	// 송신자 사번 
					, gdsUser.GetColumn(0,"USER_NM")	// 송신자 성명
					, sReceivePhoneNumber[i] 			// 수신자 번호
					, sReceiveName[i]					// 수신자명
					, strDesc );						// 문자내용(80Bytes이하)
					// 수신자아이디넣기

   }
   fnc_Message(SNIS_CRA_B999, "N","SMS발송이 완료되었습니다!");
}

// 티켓상태 로드후 필터링
function dsTicStas_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if( nReason == 0){
		obj.fireEvent = false;
		obj.filter("CD_TERM2=='RSM6050'");
		obj.fireEvent = true;
		cbStatus.Value = "001";
	}

}

// 접수
function btnRcpt_OnClick(obj)
{
	if ( !fcSaveCheck() ) return;   

	if ( !fcSaveReqCheck() ) return;   

	if( !fnc_Message(SNIS_CRA_B999, "Y","접수처리를 하시겠습니까?")) return; 



	//첨부파일 서버 업로드 
	if( dsUploadFile.GetRowCount() > 0 ) {
		for ( i = 0; i < dsUploadFile.GetRowCount(); i++ ) {
			if(dsUploadFile.getColumn(i, "CHK") == "1" ) {
				var rtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
											, dsUploadFile.GetColumn(i, "FILE_PATH" )
											, dsUploadFile.GetColumn(i, "FILE_NM" ) 
											,"");

				var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsUploadFile.GetColumn(i, "FILE_PATH" ));

				if ( rtn_arr[0] != "SUCC" ) {
					alert(rtn_arr[1] + "파일 업로드 에러발생");
					return false;
				}


				dsUploadFile.SetColumn(i, "FILE_PATH"    , rtn_arr[1] );
				dsUploadFile.SetColumn(i, "FILE_SIZE"   , fileSize );

			} // end if
		}
	} // end 



	dsTsnRcptResult_S.SetColumn(0,"TIC_STAS","002");								// 접수상태
	dsTsnRcptResult_S.SetColumn(0,"RCPT_DEPT_NM",gdsUser.GetColumn(0,"TEAM_NM") );	// 접수자부서
	dsTsnRcptResult_S.SetColumn(0,"RCPT_ID"		,gdsUser.GetColumn(0,"USER_ID") );	// 접수자ID

	var sServiceName = "rsm6050-service:saveReceipt";
	var sInDataSet   = "dsTsnRcptResult_S=dsTsnRcptResult_S dsUploadFile=dsUploadFile:u";
	var sOutDataSet  = "";  	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

