function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{	

		switch ( obj.id ) {
				case "dsBrncCd" :

					if ( nReason == 0){
						obj.fireEvent = false;
						var nRow  = obj.addrow();
						obj.SetColumn(nRow,"CD","01");
						obj.SetColumn(nRow,"CD_NM","광명");

						nRow  = obj.addrow();
						obj.SetColumn(nRow,"CD","02");
						obj.SetColumn(nRow,"CD_NM","광명");

						nRow  = obj.addrow();
						obj.SetColumn(nRow,"CD","03");
						obj.SetColumn(nRow,"CD_NM","광명");

						nRow  = obj.addrow();
						obj.SetColumn(nRow,"CD","04");
						obj.SetColumn(nRow,"CD_NM","광명");

						nRow  = obj.addrow();
						obj.SetColumn(nRow,"CD","06");
						obj.SetColumn(nRow,"CD_NM","광명");

						nRow  = obj.addrow();
						obj.SetColumn(nRow,"CD","08");
						obj.SetColumn(nRow,"CD_NM","광명");

						obj.fireEvent = true;
					}
					break;

				case "dsBrncCdSearch" :
					    if( nReason == 0){
							obj.fireEvent = false;
							obj.insertrow(0);
							obj.setColumn(0,"CD","");
							obj.setColumn(0,"CD_NM","전체");		
							obj.fireEvent = true;
							cbBrncCd.Index = 0;
						}
					break;

	 }
}

function fcGrdOnCellDBLClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	switch ( obj.id ) {
		//보고서양식 선택 
		case "grdUploadFile" :

			if(dsUploadFile.GetRowType(nRow) != "insert"){
				var sLocalFileName  = fcd_GetSelectFile("Save", dsUploadFile.GetColumn(nRow, "FILE_NM"));
				var sServerFileName = dsUploadFile.GetColumn(nRow, "FILE_PATH");
				var sFileSize       = dsUploadFile.GetColumn(nRow, "FILE_SIZE");
				if ( sLocalFileName == null ) return;

				fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName, sFileSize, nRow, "PROG_BAR", dsUploadFile);
			}
			break;
	 }
}



function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch ( obj.id ) {
		// 상태 : 상태를 해지를 선택하면 조회일자를 해지일 기준으로 변경
		case "cbStatus" :
				if (cbStatus.Value == "004") {
					rdoDayType.Index = 1;			
				} else if(cbStatus.Value != "000") {
					rdoDayType.Index = 2;			
				}
			break;
		// 상태 : 상태를 해지를 선택하면 조회일자를 해지일 기준으로 변경
		case "rdoDayType" :
				if (rdoDayType.Value == "2") {
					cbStatus.Value = "004";			
				} else {
					if (cbStatus.Value == "004") {
						cbStatus.Value = "001";
					}
				}
			break;
	 }
}
