	fcd_AddParam("DEPT_ID", 'GA120000');
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcbGetDeptTree", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  사용자클릭시 사용자메뉴권한 리스트 조회
+-------------------------------------------------------------------------------------------------*/
function fcSearchAuthMenuList(obj) {

    var sServiceName = "rsy3020-service:searchMenuAuthList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsAuthListValue=dsAuthListValue ";


	var objDs;	
	if(Tab0.TabIndex == 0){
		objDs = dsOutDept;
	}else if(Tab0.TabIndex == 1){
		objDs = dsUserListValue;
	}
	var nRow = objDs.currow;

	fcd_AddParam("USER_ID", objDs.GetColumn(nRow, "USER_ID"));	
	fcd_AddParam("ADMIN_USER_ID", sUserID);		

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function fcSearchAuthMenuListByGrd(obj) {

    var sServiceName = "rsy3020-service:searchMenuAuthList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsAuthListValue=dsAuthListValue ";

	var objDs;	
	objDs = dsUserListValue;
	var nRow = objDs.currow;	
	fcd_AddParam("USER_ID", objDs.GetColumn(nRow, "USER_ID"));	
	fcd_AddParam("ADMIN_USER_ID", sUserID);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 저장가능유무처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {  

	var objDs;

	var iSelectRowCount = 0;

	if(Tab0.TabIndex == 0){
		objDs = dsOutDept;
	}else if(Tab0.TabIndex == 1){
		objDs = dsUserListValue;
	}


	for ( var i = 0; i < objDs.GetRowCount(); i++ ) {
        if ( Length(objDs.GetColumn(i, "USER_ID"))>0  && objDs.GetColumn(i, "CHK") == "1" ) {
            iSelectRowCount++;
        }
	}


	//선택된 Row Count
	//var iSelectRowCount = fnc_GetColumnValueCount(objDs, "CHK", "1");


	if ( iSelectRowCount <= 0 ) {
        fnc_Message(SNIS_CRA_Z003, "N");
        return false;
	}

	return true;

}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

    if ( !fcSaveCheck() ) return;

    var sDsId;

	if(Tab0.TabIndex == 0){
		sDsId = "dsOutDept";
	}else if(Tab0.TabIndex == 1){
		sDsId = "dsUserListValue";
	}


    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    

    sServiceName = "rsy3020-service:saveAuth";
    sInDataSet   = "dsUserListValue="+sDsId+":u ";
    sInDataSet  += "dsAuthListValue=dsAuthListValue:u ";    
    sOutDataSet  = "";

    for ( var i = 0; i < dsAuthListValue.rowcount; i++ ) {        
        if(    dsAuthListValue.getColumn(i, "SRCH_YN") == 1
            || dsAuthListValue.getColumn(i, "INST_YN") == 1
            || dsAuthListValue.getColumn(i, "APRV_YN") == 1) {

            if(dsAuthListValue.getColumn(i, "SRCH_YN") != 1) dsAuthListValue.setColumn(i, "SRCH_YN", 0);
            if(dsAuthListValue.getColumn(i, "INST_YN") != 1) dsAuthListValue.setColumn(i, "INST_YN", 0);
            if(dsAuthListValue.getColumn(i, "APRV_YN") != 1) dsAuthListValue.setColumn(i, "APRV_YN", 0);           

        }
    }


    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


