function dsOutMNGrp_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{    
    dsOutMNGrp.InsertRow(0);
	dsOutMNGrp.SetColumn(0, "MN_NM", " ");

	dsOutMNGrp.InsertRow(1);
	dsOutMNGrp.SetColumn(1, "MN_NM", "전체");
	cboMnGrpCd.index = 0;
}

function dsOutTeamNm_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	dsOutTeamNm.InsertRow(0);
	dsOutTeamNm.SetColumn(0, "TEAM_NM", "전체");
	cboTeamNm.index = 0;
}

function dsUserListValue_OnRowPosChanged(obj,nOldRow,nRow)
{
	fcSearchAuthMenuList(obj);
}

function dsAuthListValue_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var chkYN = dsAuthListValue.GetColumn(nRow, strColumnID);

	if(strColumnID == "SRCH_YN" && chkYN == 0) {
        dsAuthListValue.SetColumn(nRow, "INST_YN", 0);
        dsAuthListValue.SetColumn(nRow, "INPT_YN", 0);
        dsAuthListValue.SetColumn(nRow, "APRO_YN", 0);
	}
	if(strColumnID != "SRCH_YN" && chkYN == 1) {
        dsAuthListValue.SetColumn(nRow, "SRCH_YN", 1);
	}
}

function cboMnGrpCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{    
	for ( var i = 0; i < dsAuthListValue.rowcount; i++ ) {
        if(cboMnGrpCd.Index == 1) {
            dsAuthListValue.setColumn(i, "SRCH_YN", chkSrch.Value);
            dsAuthListValue.setColumn(i, "INST_YN", chkSave.Value);
            dsAuthListValue.setColumn(i, "APRV_YN", chkApro.Value);
        } else {
            if(strCode == dsAuthListValue.getColumn(i, "UPUP_MN_ID")) {
                dsAuthListValue.setColumn(i, "SRCH_YN", chkSrch.Value);
                dsAuthListValue.setColumn(i, "INST_YN", chkSave.Value);
                dsAuthListValue.setColumn(i, "APRV_YN", chkApro.Value);           
            } 
        }
	}
}

function chk_OnClick(obj,strValue)
{
    for ( var i = 0; i < dsAuthListValue.rowcount; i++ ) {
        if(cboMnGrpCd.Index == 1) {                                                 //전체이면...
            fcCommonChk(obj, i);
        } else {
            if(cboMnGrpCd.Value == dsAuthListValue.getColumn(i, "UPUP_MN_ID")) {    //선택메뉴일때...
                fcCommonChk(obj, i);     
            } 
        }

	}
}

function fcCommonChk(obj, i)
{
    if(obj.id == "chkSrch" && obj.value == 0) {              //조회 언체크하면 저장,승인 자동 언체크
        chkSave.Value = 0;
        chkApro.Value = 0;
        dsAuthListValue.setColumn(i, "SRCH_YN", 0);
        dsAuthListValue.setColumn(i, "INST_YN", 0);
        dsAuthListValue.setColumn(i, "APRV_YN", 0);
    } else if(obj.id != "chkSrch" && obj.value == 1) {       //저장,승인 체크하면 조회부분 자동체크되도록
        chkSrch.Value = 1;
        dsAuthListValue.setColumn(i, "SRCH_YN", chkSrch.Value);
        dsAuthListValue.setColumn(i, "INST_YN", chkSave.Value);
        dsAuthListValue.setColumn(i, "APRV_YN", chkApro.Value);
    } else {    
        dsAuthListValue.setColumn(i, "SRCH_YN", chkSrch.Value);
        dsAuthListValue.setColumn(i, "INST_YN", chkSave.Value);
        dsAuthListValue.setColumn(i, "APRV_YN", chkApro.Value);
    }
}


//메뉴명 셀을 클릭하면 해당 메뉴의 권한있는 유저를 표시한다.
function grdMenuList_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    //메뉴명을 클릭했을 경우만 실행
    if (nCell == 3) {
        if ( !fcSearchCheck(obj) ) return;

        var sServiceName = "rsy3020-service:searchUserList";
        var sInDataSet   = "";
        var sOutDataSet  = "dsUserListValue=dsUserListValue ";

        fcd_AddParam("MN_ID",   dsAuthListValue.GetColumn(nRow, "MN_ID"));
        fcd_AddParam("USER_NM", edSearchTxt.text);
        fcd_AddParam("TEAM_CD", cboTeamNm.Value);

        fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
    }

}

function dsOutDept_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	dsOutDept.FireEvent = false; 
	var chkVal = dsOutDept.GetColumn(nRow, "CHK");
	var deptCd = dsOutDept.GetColumn(nRow, "DEPT_CD"); 
	var levl = dsOutDept.GetColumn(nRow, "LEVL"); 

	for (var j=nRow+1;j<dsOutDept.RowCount();j++)
	{ 
		if ( dsOutDept.GetColumn(j, "LEVL") <= levl ){
			break;
		}

		dsOutDept.SetColumn(j, "CHK",  chkVal);

	}

	dsOutDept.FireEvent = true; 
}

