/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'TTTFTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);


	//구축년도 설정
	fcSetBuildYear();


	//초괴화면 : 기본정보
    tbSw.TabIndex = 0;


	cboSearchBuildYear.SetFocus();

    fcSearch();
}
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	// 자료 변경 여부를 체크하고자 하는 DataSet Object를 넘긴다.
	var sDataset = "dsInfraSw";
	if (   !fnc_FormUnloadCheck(sDataset  , sFormId) ) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	// Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}


}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  초기화 버튼 클릭 時 처리, 조회전 CLEAR 
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

	if(obj.ID == "btnSearch"){  //조회전 초기화 
		dsInfraSw.ClearData();

		tbSw.tbEquipHist.divEquipHist.fcInit();
	}else if(obj.ID == "dsInfraSw"){ // 
		tbSw.tbEquipHist.divEquipHist.fcInit();

	}else if(obj.ID == "btnCracel"){ // 초기화버튼 
		dsInfraSw.ClearData();

		tbSw.tbEquipHist.divEquipHist.fcInit();

		tbSw.TabIndex = 0;
	}else{
		dsInfraSw.ClearData();

		tbSw.tbEquipHist.divEquipHist.fcInit();
	}

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj,sDistNo,sEquipGbn) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;


	switch ( obj.Id ) {
		case "btnSearch": // 조회버튼 클릭시 

			 sServiceName = "rsy4010-service:searchInfraSw";
			 sInDataSet   = "";
			 sOutDataSet  = "dsInfraSw=dsInfraSw ";

			 //조회조건 : 
			 fcd_AddParam("BUILD_YEAR", cboSearchBuildYear.Value);
			 fcd_AddParam("SW_NM", edSearchSwNm.Text);
			 fcd_AddParam("DEV_LANG", edSearchDevLang.Text);
			 fcd_AddParam("DEV_COMP", edSearchDevComp.Text);

			 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);


			break;

		case "dsInfraSw" :	 // GRID ROWCHANGE EVENT 시 

			 tbSw.tbEquipHist.divEquipHist.fcSearch(obj,sDistNo,sEquipGbn);

			 break;

		default:		// COMBO CHANGE 등 이벤트시 
			 sServiceName = "rsy4010-service:searchInfraSw";
			 sInDataSet   = "";
			 sOutDataSet  = "dsInfraSw=dsInfraSw ";

			 //조회조건 : 
			 fcd_AddParam("BUILD_YEAR", cboSearchBuildYear.Value);
			 fcd_AddParam("SW_NM", edSearchSwNm.Text);
			 fcd_AddParam("DEV_LANG", edSearchDevLang.Text);
			 fcd_AddParam("DEV_COMP", edSearchDevComp.Text);

			 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);


			break;

	}

}




/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크

	if ( !fnc_DatasetChangeCheck(dsInfraSw) && !tbSw.tbEquipHist.divEquipHist.fcSaveCntCheck() ) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsInfraSw;
	sMandatoryColID   = "SW_NM|BUILD_YEAR";
	sMandatoryColName = "SW명|구축년도";
	oGrid             =  grdInfraSw;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}


	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {


	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";


	if ( !fcSaveCheck() ) return;
	if ( !tbSw.tbEquipHist.divEquipHist.fcSaveCheck(obj) ) return;

	sServiceName = "rsy4010-service:saveInfraSw";
	sInDataSet   = "dsInfraSw=dsInfraSw:u ";

	sOutDataSet  = "dsInfraSw=dsInfraSw ";


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

	tbSw.tbEquipHist.divEquipHist.fcSave(obj);


}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	// DataSet의 Header 정보가 있을 때  Add 처리
	var sCurYear  = ParseInt(fcd_GetSysdate("YYYY"));

	switch ( obj.GetForm().id ) {
		case "grpBtnRow" :
			if ( fnc_DatasetChangeCheck(dsInfraSw) ) {
				//fnc_Message(L_COM_ALT_0024, "N", "현재 작업중인 행이 존재하기 때문에\r\n 행을 추가할 수 ");
				//return;
			}

			nRow = dsInfraSw.AddRow(); // Record Add

			dsInfraSw.SetColumn(nRow,"BUILD_YEAR",sCurYear);


			grdInfraSw.SetFocus(); // 입력 첫 항목으로 Focus

			tbSw.TabIndex = 0;

			tbSw.tbInfraSw.edSwNm.SetFocus();

			break;
	}
}


/*-------------------------------------------------------------------------------------------------+
>>  행삽입 버튼 클릭 時 처리
