/*-------------------------------------------------------------------------------------------------+
>>  dataset 에서 OnRowPosChanged 처리
+-------------------------------------------------------------------------------------------------*/
function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{	
	switch ( obj.id ) {
		//장비이력 목록 

		case "dsInfraSw" :
		  if(nRow>-1){	
				if(dsInfraSw.GetRowType(dsInfraSw.row) ==  "insert"){

						tbSw.tbEquipHist.divEquipHist.fcInit();

						//신규 행일 경우   초기화 
						sEquipDistNo = "";

				}else if(dsInfraSw.GetRowType(dsInfraSw.row) ==  "normal" || dsInfraSw.GetRowType(dsInfraSw.row) ==  "update"){

						fcd_AddParam("EQUIP_DIST_NO", dsInfraSw.GetColumn(nRow,"SW_DIST_NO"));
						fcd_AddParam("CHECK_EQUIP_GBN", "002");

						fcSearch(obj,dsInfraSw.GetColumn(nRow,"SW_DIST_NO"),"002");

						sEquipDistNo = dsInfraSw.GetColumn(nRow,"SW_DIST_NO");

				}

				tbSw.tbInfraSw.edSwNm.SetFocus();
		  }

		  break;


	}
}

