/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'TTTTTT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);


	//Div공통코드 조회
	Div0.url = DIV_CODE_URL;

	setTimer(1, 100); 
	/*
		Div 가 Loading 시간이 걸린다. 
		반드시 timer 로 처리해야함.
	*/

	fcSearch();

}


/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "";
	if (   !fnc_FormUnloadCheck(sDataset  , sFormId) ) return false;
}


function dsOutSDept_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
    dsOutSDept.InsertRow(0);
	dsOutSDept.SetColumn(0, "CD_NM", "전체");
	Combo0.index = 0;
}


function fcSearch(obj) {

	var sServiceName = "rsy1010-service:searchGrpCode";
	var sInDataSet   = "";
	var sOutDataSet  = "dsGrpCd=dsGrpCd ";
		sOutDataSet += "dsSpecCd=dsSpecCd ";

	fcd_AddParam(Trim("DEL_YN"), "N");
	fcd_AddParam("GRP_NM", "");
	fcd_AddParam("SPC_NM", "");

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

function fcGetSpecCode() {
	var sServiceName = "rsy1010-service:searchSpecCode";
	var sInDataSet   = "";
	var sOutDataSet  = "dsSpecCd=dsSpecCd";

	fcd_AddParam("GRP_CD", dsGrpCd.getColumn(dsGrpCd.row, "GRP_CD"));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function dsGrpCd_OnRowPosChanged(obj,nOldRow,nRow)
{
	dsSpecCd.ClearData();	//상세코드 초기화
	fcGetSpecCode();		//상세코드 조회
}


/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

    dsMasterValue.AddRow();             //Record Add
    grdMaster.SetFocus();               //입력 첫 항목으로 Focus

}

/*-------------------------------------------------------------------------------------------------+
>>  행삽입 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInsRow(obj) {

    dsMasterValue.InsertRow(dsMasterValue.GetCurrow());           //Record Ins
    grdMaster.SetFocus();               //입력 첫 항목으로 Focus

}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리 	

	if ( !fnc_DeleteCheck(dsMasterValue, "chk", "1") ) return;
        //처리로직
		break;


}


