//클릭 이벤트 처리
function fcClickEvent(obj) {
	var logImgUrl = "";
	var sSubUrl   = "";          // 레포트 파일이 있는 서브디렉토리
	var sRptNm    = "";          // 레포트 파일명
	var sDataSet  = "";          // "UBI 쿼리명, MIP Dataset명"
	var sArg      = "";
	var sRptDrct  = 0;      

	switch ( obj.ID ) {
		//질문 메세지 버튼 클릭
		case "Button0" :	
			xxx = fnc_Message(SNIS_COM_1004,"Yes:No","테스트");

			if(xxx == 1) alert("Yes");
			if(xxx == 0) alert("No");
			break;

		/*
		>> 전자결재 버튼 클릭 이벤트
		>> 공통함수 호출 : fcd_ExecApproval()
		>> 파라메터 : 업무구분, 기준년도, 회차, 일차, 경주번호, 순번, 기안자, 문서제목, 데이터셋ID, 내용(컴포넌트ID)
		>> 파라메터 상세
			1. 업무구분 : EA(확정검사), EB(낙차부상현황), BA(후보선수확정서),BB(배정선수교체현황)
						  CA(경주순위확정), CB(경주확정), CC(낙차/사고보고서)
						  CD(전담선두원확정), CE(일차별전담선두원확정)
			2. 기준년도, 회차 : not null
			3. 일차, 경주번호, 순번 : null 가능(일차=00, 경주번호=0)
			4. 기안자 : 로그인한 user_id
			5. 문서제목 : 결재 보고서 제목
			6. 데이터셋ID : 첨부문서(엑셀)로 생성할 데이터셋 ID   ==> grdMaster(엑셀)/rptViewer(PDF)
			7. 내용 : html 파일로 생성될 내용의 오브젝트 ID(textArea)
			8. 파일Type : PDF, HWP, Excel(Excel일 경우는 생략가능)
			* PDF, HWP일 경우는 UBI Report일 경우만 가능
		*/
		case "ImgApproval" :
			fcd_ExecApproval("EC","2009","03","","","","05074","낙차사고_결재테스트",rptViewer,TxtContents, "PDF");
			//fcd_ExecApproval("BA","2009","01","","","","77777","후보선수확정서_결재테스트",grdMaster);
			break;


		/*
		>> 알림 버튼 클릭 이벤트
		>> 공통함수 호출 : fcd_SendMessage()
		>> 파라메터 : 발신사원번호, 수신사원번호, 제목, 링크정보, 내용
		>> 파라메터 상세
			1. SND_USER : 05074(조성문) 보내는사람
			2. RCV_USER : b0105(이영상) 받는사람
			3. DOC_NAME : 테스트 메신져
			4. DOC_URL : JSY0020^b0105 ==> (메뉴ID^받는USERID)
			5. DOC_DESC : 테스트 메신져 입니다. 많은 이용 부탁드립니다.
		*/
		case "ImgConfirm" :
			fcd_SendMessage('05074','b0102','테스트 메신져','RSY1010^b0102','테스트 메신져 입니다. 많은 이용 부탁드립니다.');
			break;


		//알림관리화면 호출(팝업호출)
		//>> ALARM_CD='201'('201' = 알림 업무구분코드)
		case "Image2" :
			Dialog("common::comAlarmPopup.xml", "ALARM_CD='201'", 334, 443, "");
			break;


		//SMS보내기 버튼 클릭
		case "ImSMS" :
			alert("단순하게 테스트만 해봤다. ^^..... ");

			//파라메터 세팅하고 공통부분 구현해서 사용해~~~
			var strSendPhoneNumber = "";		// 송신자 번호
			var strSendUserId = "";			// 송신자 사번			(예:05073)
			var strSendUserName = "";		// 송신자 성명(예:홍길동)	
			var strReceivePhoneNumber = "";	// 수신자 번호, 여러명인경우 ; 로 구분 (예:01020717775)
			var strReceiveName = "";			// 수신자명, 여러명인경우 ; 로 구분
			var strSendSubject = "";			// 전송할 문자. (80Bytes이하)
			fcd_SendSms(strSendPhoneNumber, strSendUserId, strSendUserName, strReceivePhoneNumber,strReceiveName, strSendSubject);

			break;


		//인쇄 버튼 클릭
		case "ImReportVrt" :
			logImgUrl = GBL_REPORT_URL+"log.jpg";
			sSubUrl   = "";                                             // 레포트 파일이 있는 서브디렉토리
			sRptNm    = "sample.jrf";                                   // 레포트 파일명
			sDataSet  = "dsDetlEduAssign=dsSpecCd";          			// "UBI 쿼리명, MIP Dataset명"
		    sArg      = " StndYear=테스트";
			sArg     += " Test=UBI 개발 교육 필요합니다.";		
			sRptDrct  = 0;                                              // 출력방향(0 : 수직, 1 : 수평)

			fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
			break;

		case "ImReportHrz" :
			logImgUrl = GBL_REPORT_URL+"log.jpg";
			sSubUrl   = "";                                             // 레포트 파일이 있는 서브디렉토리
			sRptNm    = "sample3.jrf";                                  // 레포트 파일명
			sDataSet  = "dsDetlEduAssign=dsSpecCd";          			// "UBI 쿼리명, MIP Dataset명"
		    sArg      = " StndYear=테스트";
			sArg     += " Test=UBI 개발 교육 필요합니다.";		
			sRptDrct  = 1;                                              // 출력방향(0 : 수직, 1 : 수평)

			fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
			break;
		case "img_move" :
			global.FrameBottom.fcOpenMdiForm("RSY4040", "IP관리", "frm_rsy::RSY4040.xml");
			break;


	}

}


