/*-------------------------------------------------------------------------------------------------+
>>  그리드 팝업 조회
+-------------------------------------------------------------------------------------------------*/
function Grid0_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{

	var sFormId 	= "";
	var sArgs 		= "";
	var arrReturn   = array(4);

	if (nCell == 0) {
		sFormId = "common::comDeptTree.xml";
		sArgs = "";	

		arrReturn = Dialog(sFormId, sArgs, 334, 443, "");

		dsCommonCd.SetColumn(dsCommonCd.currow,"code",arrReturn[0]);
		dsCommonCd.SetColumn(dsCommonCd.currow,"codeNm",arrReturn[1]);

	}else if (nCell == 2) {
		fnc_SetPopupDivCalendar(obj, nRow, nCell);

	}
}

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
}
